mapper.select:
select  task_id
from 2dfire.t_schedule_task
where url = #{url,jdbcType=VARCHAR}
 and method = #{method,jdbcType=VARCHAR}
 and cron = #{cron,jdbcType=VARCHAR}
 and state='1' limit 1

select count(*) cnt
from 2dfire.t_schedule_task

  
   and task_name = #{taskName,jdbcType=VARCHAR}
  
  
   and task_type = #{taskType,jdbcType=CHAR}
  
  
   and url = #{url,jdbcType=VARCHAR}
  
  
   and method = #{method,jdbcType=VARCHAR}
  
  
   and post_param = #{postParam,jdbcType=VARCHAR}
  
  
   and cron = #{cron,jdbcType=VARCHAR}
  
  
   and initial_delay = #{initialDelay,jdbcType=VARCHAR}
  
  
   and fixed_delay = #{fixedDelay,jdbcType=VARCHAR}
  
  
   and fixed_rate = #{fixedRate,jdbcType=VARCHAR}
  
  
   and is_immedi_exe = #{isImmediExe,jdbcType=CHAR}
  
  
   and time_zone = #{timeZone,jdbcType=VARCHAR}
  
  
   and next_execute = #{nextExecute,jdbcType=VARCHAR}
  
  
   and last_execute = #{lastExecute,jdbcType=VARCHAR}
  
  
   and state = #{state,jdbcType=CHAR}

